sparql.en:
# Query 2

DEFINE sql:select-option "order"
PREFIX xsd: &lt;http://www.w3.org/2001/XMLSchema#&gt; 
PREFIX icgc: &lt;http://icgc.link/vocab/&gt;
PREFIX hgnc_vocabulary: &lt;http://bio2rdf.org/hgnc_vocabulary:&gt;
PREFIX bio2rdf_vocabulary: &lt;http://bio2rdf.org/bio2rdf_vocabulary:&gt;
PREFIX bio2rdf-ensembl: &lt;http://bio2rdf.org/ensembl_vocabulary:&gt;
PREFIX owl: &lt;http://www.w3.org/2002/07/owl#&gt;
PREFIX dct: &lt;http://purl.org/dc/terms/&gt;

SELECT 
 (?mutation_id AS ?ID)
 (CONCAT("chr", ?chromosome, ":g.", STR(?chromosome_start), ":g.",
  ?mutated_from_allele, "&gt;", ?mutated_to_allele) AS ?DNA_Change)
 (?mutation_type AS ?Type)
 (COUNT(DISTINCT ?donor) AS ?donors)
FROM &lt;http://icgc.link/release_20&gt;
{
  ?project icgc:project_code "BOCA-UK" ;
    a &lt;http://icgc.link/Project&gt; ;
    icgc:primary_site ?primary_site .
  ?donor icgc:project ?project .
  ?detection icgc:donor ?donor ;
    a &lt;http://icgc.link/Detection&gt; ;
    icgc:mutation ?mutation .
  ?mutation a &lt;http://icgc.link/Mutation&gt; ;
    icgc:mutation_id ?mutation_id ;
    icgc:mutation_type ?mutation_type ;
    icgc:chromosome ?chromosome ;
    icgc:chromosome_start ?chromosome_start ;
    icgc:mutated_from_allele ?mutated_from_allele ;
    icgc:mutated_to_allele ?mutated_to_allele .
  ?effect icgc:mutation ?mutation ;
    a &lt;http://icgc.link/Effect&gt; ;
    icgc:gene_affected ?gene .
  ?gene a &lt;http://icgc.link/Gene&gt;
}
GROUP BY ?mutation_id ?chromosome ?chromosome_start ?mutated_from_allele ?mutated_to_allele ?mutation_type
ORDER BY DESC(?donors)
LIMIT 20

sparql.ja:
# Query 2
#
# イギリスの骨肉腫のプロジェクト「BOCA-UK」で多くの症例で検出された変異上位20個

DEFINE sql:select-option "order"
PREFIX xsd: &lt;http://www.w3.org/2001/XMLSchema#&gt; 
PREFIX icgc: &lt;http://icgc.link/vocab/&gt;
PREFIX hgnc_vocabulary: &lt;http://bio2rdf.org/hgnc_vocabulary:&gt;
PREFIX bio2rdf_vocabulary: &lt;http://bio2rdf.org/bio2rdf_vocabulary:&gt;
PREFIX bio2rdf-ensembl: &lt;http://bio2rdf.org/ensembl_vocabulary:&gt;
PREFIX owl: &lt;http://www.w3.org/2002/07/owl#&gt;
PREFIX dct: &lt;http://purl.org/dc/terms/&gt;

SELECT 
 (?mutation_id AS ?ID)
 (CONCAT("chr", ?chromosome, ":g.", STR(?chromosome_start), ":g.",
  ?mutated_from_allele, "&gt;", ?mutated_to_allele) AS ?DNA_Change)
 (?mutation_type AS ?Type)
 (COUNT(DISTINCT ?donor) AS ?donors)
FROM &lt;http://icgc.link/release_20&gt;
{
  ?project icgc:project_code "BOCA-UK" ;
    a &lt;http://icgc.link/Project&gt; ;
    icgc:primary_site ?primary_site .
  ?donor icgc:project ?project .
  ?detection icgc:donor ?donor ;
    a &lt;http://icgc.link/Detection&gt; ;
    icgc:mutation ?mutation .
  ?mutation a &lt;http://icgc.link/Mutation&gt; ;
    icgc:mutation_id ?mutation_id ;
    icgc:mutation_type ?mutation_type ;
    icgc:chromosome ?chromosome ;
    icgc:chromosome_start ?chromosome_start ;
    icgc:mutated_from_allele ?mutated_from_allele ;
    icgc:mutated_to_allele ?mutated_to_allele .
  ?effect icgc:mutation ?mutation ;
    a &lt;http://icgc.link/Effect&gt; ;
    icgc:gene_affected ?gene .
  ?gene a &lt;http://icgc.link/Gene&gt;
}
GROUP BY ?mutation_id ?chromosome ?chromosome_start ?mutated_from_allele ?mutated_to_allele ?mutation_type
ORDER BY DESC(?donors)
LIMIT 20

