FROM &lt;http://mbgd.genome.ad.jp/rdf/resource/xref_uniprot&gt;
FROM &lt;http://mbgd.genome.ad.jp/rdf/resource/gene&gt;
FROM &lt;http://mbgd.genome.ad.jp/rdf/resource/organism&gt;
