sparql.en:
#  Search for a DDBJ entry "CP002459.1" and the metadata.
#  Note the SPARQL endpoint for DDBJ is https://integbio.jp/rdf/ddbj/sparql .

PREFIX rdf:&lt;http://www.w3.org/1999/02/22-rdf-syntax-ns#&gt;
PREFIX rdfs:&lt;http://www.w3.org/2000/01/rdf-schema#&gt;
PREFIX insdc:&lt;http://ddbj.nig.ac.jp/ontologies/nucleotide/&gt;

SELECT
  ?predicate ?object
WHERE
{
  VALUES ?entry { &lt;http://identifiers.org/insdc/CP002459.1&gt; }
  VALUES ?predicate { 
    insdc:comment
    insdc:definition
    insdc:division
    insdc:organism
    insdc:sequence_date
    insdc:sequence_version
    insdc:source
    insdc:taxonomy
    insdc:dblink
  }                      
  ?entry rdf:type insdc:Entry. 
  ?entry ?predicate ?object.
}

