sparql.en:
# Select SMILES and IUPAC InChI of FDA approved drugs stored in PubChem
# Note the SPARQL endpoint for PubChem is https://integbio.jp/rdf/pubchem/sparql .
PREFIX cpd: &lt;http://rdf.ncbi.nlm.nih.gov/pubchem/compound/&gt;
PREFIX rdf: &lt;http://www.w3.org/1999/02/22-rdf-syntax-ns#&gt;
PREFIX rdfs: &lt;http://www.w3.org/2000/01/rdf-schema#&gt;
PREFIX sio: &lt;http://semanticscience.org/resource/&gt;
PREFIX obo: &lt;http://purl.obolibrary.org/obo/&gt;
PREFIX descriptor: &lt;http://rdf.ncbi.nlm.nih.gov/pubchem/descriptor/&gt;
PREFIX compound: &lt;http://rdf.ncbi.nlm.nih.gov/pubchem/compound/&gt;
PREFIX pubchemv: &lt;http://rdf.ncbi.nlm.nih.gov/pubchem/vocabulary#&gt;

SELECT ?cid ?canonical_smiles ?iupac_inchi
WHERE {
  ?cid a &lt;http://www.biopax.org/release/biopax-level3.owl#SmallMolecule&gt; ;
  obo:has-role pubchemv:FDAApprovedDrugs ;
  sio:has-attribute ?smiles ;
  sio:has-attribute ?inchi .
?smiles a sio:CHEMINF_000376 ;
        sio:has-value ?canonical_smiles .
?inchi a sio:CHEMINF_000396 ;
        sio:has-value ?iupac_inchi
}
LIMIT 100

