sparql.en:
# Select all UniProt entries and amino acid sequences (including isoforms), for Bacillus subtilis (strain 168)
# Note the SPARQL endpoint for UniProt is https://integbio.jp/rdf/uniprot/sparql .
PREFIX schema: &lt;http://schema.org/&gt;
PREFIX rdfs: &lt;http://www.w3.org/2000/01/rdf-schema#&gt;
PREFIX rdf: &lt;http://www.w3.org/1999/02/22-rdf-syntax-ns#&gt;
PREFIX taxon: &lt;http://purl.uniprot.org/taxonomy/&gt;
PREFIX up: &lt;http://purl.uniprot.org/core/&gt;

SELECT ?protein ?isoform ?aa_sequence
WHERE
{
    ?protein a up:Protein .
    ?protein up:organism taxon:224308 .
    ?protein up:sequence ?isoform .
    ?isoform rdf:value ?aa_sequence .
}

